import {Box} from "@chakra-ui/react";
import {useTranslation} from "react-i18next";
import {useRef} from "react";

export default function Homepage() {
    const elementRef = useRef(null);
    const { t, i18n } = useTranslation();

    const featureItem: any = {
        flexDirection: "column",
        textAlign: { base: "left", lg: "unset" },
        alignItems: "baseline",
    };

    return (
        <Box>
            <Banner
                title={t("Homepage.Banner.Title")}
                image="/assets/images/banners/banner.jpg"
                h="700px"
                text={
                    <Text letterSpacing="-1px">
                        <Trans i18nKey="Homepage.Banner.Content" />
                    </Text>
                }
                buttonText={t("Homepage.Start")}
                onClick={() => {
                    window.open(
                        `${routeWithParameters(urls.Login)}${
                            window.location.search.length > 0
                                ? `&lang=${i18n.language}&signup=true`
                                : `?lang=${i18n.language}&signup=true`
                        }`
                    );
                }}
            />
        </Box>
    );
}
