import {
  Box,
  Flex,
  Link as CLink,
  Text,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Desktop } from "../../utils/hooks";
import { Language, urls } from "../../utils/language";

export const routeWithParameters = (url: string): string => {
  const searchParams = window.location.search;
  if (searchParams !== "") {
    return `${url}${searchParams}`;
  } else {
    return url;
  }
};

export function Header() {
  const [activeMenu, setActiveMenu] = React.useState(2);
  const [activeLink, setActiveLink] = React.useState(0);
  const { t, i18n } = useTranslation();

  const linkImages = [
    "5.jpg",
    "1.jpg",
    "2.jpg",
    "8.jpg",
    "12.jpg",
    "7.png",
    "3.jpg",
    "6.jpg",
    "11.jpg",
    "10.jpg",
    "9.jpg",
  ];

  const headerLink = {
    color: "white",
    cursor: "pointer",
    marginTop: "12px",
    marginBottom: "12px",
    _hover: {
      color: "red.500",
    },
  };

  const mobileHeaderLink = {
    color: "white",
    cursor: "pointer",
    _hover: {
      color: "red.500",
    },
  };

  return (
      <>
        {/* Desktop Menu */}
        <Desktop>
          <Box bg="black" pos="fixed" top="0" w="100%" zIndex={10}>
            <Flex
                d="grid"
                gridTemplateColumns="5fr 2fr 2fr 1fr 2fr 3fr 1fr"
                alignItems="center"
                maxW="1200px"
                p="4"
                m="0 auto"
            >
              <Link to={routeWithParameters(urls.Homepage)}> Fish to Whale</Link>

              <Box
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.Login)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.Login")}
                </a>
              </Box>

              <Box
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.HowToStart)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.HowToStart")}
                </a>
              </Box>


                <Box
                    cursor="pointer"
                    color="black"
                    _hover={{
                      color: "red.500",
                    }}
                    justifyContent="center"
                    fontWeight="600"
                >
                  <a
                      href={routeWithParameters(urls.Contact)}
                      target="_blank"
                      rel="noopener"
                  >
                    {t("Menu.Contact")}
                  </a>
                </Box>

              <motion.div {...headerAnimate}>
                <Flex>
                  {
                    ['tr', 'en'].map((item, index) => {
                      return (
                          <Text
                              key={index.toString()}
                              fontWeight="600"
                              cursor="pointer"
                              marginRight="5px"
                              marginLeft="5px"
                              color="#fff"
                              onClick={() => {
                                Language.change(item);
                              }}
                          >
                            {item.toUpperCase()}
                          </Text>
                      );
                    })}
                </Flex>
              </motion.div>
            </Flex>
            {activeMenu === 0 && (
                <Flex
                    height="250px"
                    w="100%"
                    bg="black"
                    pos="absolute"
                    zIndex={5}
                    onMouseLeave={() => setActiveMenu(2)}
                    borderTop="0.5px solid #ff5555"
                >
                  <Flex w={{ base: "auto", lg: "1200px" }} m="0 auto" py="25px">
                    <Animate fade>
                      <Box
                          backgroundImage={`url('/assets/images/menus/${linkImages[activeLink]}')`}
                          backgroundPosition="center"
                          backgroundSize="cover"
                          w="370px"
                          h="200px"
                          borderRadius="8px"
                      />
                    </Animate>
                    <Flex
                        color="white"
                        flexDirection="column"
                        m="auto 34px"
                        fontWeight="500"
                    >
                      <Animate>
                        <PseudoBox {...headerLink}>
                          <Link
                              to={routeWithParameters(urls.LTLTransport)}
                              onMouseEnter={() => {
                                setActiveLink(0);
                              }}
                          >
                            {t("Menu.LTL")}
                          </Link>
                        </PseudoBox>
                        <PseudoBox {...headerLink}>
                          <Link
                              to={routeWithParameters(urls.FTLTransport)}
                              onMouseEnter={() => {
                                setActiveLink(1);
                              }}
                          >
                            {t("Menu.FTL")}
                          </Link>
                        </PseudoBox>
                        <PseudoBox
                            {...headerLink}
                            onMouseEnter={() => {
                              setActiveLink(2);
                            }}
                        >
                          <Link to={routeWithParameters(urls.ContractShipping)}>
                            {t("Menu.ContractShipping")}
                          </Link>
                        </PseudoBox>
                        <PseudoBox
                            {...headerLink}
                            onMouseEnter={() => {
                              setActiveLink(3);
                            }}
                        >
                          <Link to={routeWithParameters(urls.ForeignTransport)}>
                            {t("Menu.ForeignTransport")}
                          </Link>
                        </PseudoBox>
                      </Animate>
                    </Flex>
                  </Flex>
                </Flex>
            )}
            {activeMenu === 1 && (
                <Flex
                    height="250px"
                    w="100%"
                    bg="black"
                    pos="absolute"
                    zIndex={5}
                    onMouseLeave={() => setActiveMenu(2)}
                    borderTop="0.5px solid #ff5555"
                >
                  <Flex w={{ base: "auto", lg: "1200px" }} m="0 auto" py="25px">
                    <Animate fade>
                      <Box
                          backgroundImage={`url('/assets/images/menus/${linkImages[activeLink]}')`}
                          backgroundPosition="center"
                          backgroundSize="cover"
                          w="370px"
                          h="200px"
                          borderRadius="8px"
                      />
                    </Animate>
                    <Flex
                        color="white"
                        // lineHeight="2.80"
                        fontWeight="500"
                        // pt="15px"
                    >
                      <Box ml="34px">
                        <Animate>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(4);
                              }}
                          >
                            <Link to={routeWithParameters(urls.HowDoesItWork)}>
                              {t("Menu.HowDoesItWork")}
                            </Link>
                          </PseudoBox>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(5);
                              }}
                          >
                            <Link to={routeWithParameters(urls.Technology)}>
                              {t("Menu.Technology")}
                            </Link>
                          </PseudoBox>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(6);
                              }}
                          >
                            <Link to={routeWithParameters(urls.Customers)}>
                              {t("Menu.Customers")}
                            </Link>
                          </PseudoBox>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(7);
                              }}
                          >
                            <Link to={routeWithParameters(urls.BeACarrier)}>
                              {t("Menu.BeACarrier")}
                            </Link>
                          </PseudoBox>
                        </Animate>
                      </Box>
                      <Box ml="70px">
                        <Animate>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(8);
                              }}
                          >
                            <Link to={routeWithParameters(urls.Media)}>
                              {t("Menu.Media")}
                            </Link>
                          </PseudoBox>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(9);
                              }}
                          >
                            <Link to={routeWithParameters(urls.Support)}>
                              {t("Menu.Support")}
                            </Link>
                          </PseudoBox>
                          <PseudoBox
                              {...headerLink}
                              onMouseEnter={() => {
                                setActiveLink(10);
                              }}
                          >
                            <Link to={routeWithParameters(urls.Contact)}>
                              {t("Menu.Contact")}
                            </Link>
                          </PseudoBox>
