import {
  Box,
  Flex,
  Link as CLink,
  Text,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Desktop } from "../../utils/hooks";
import { Language, urls } from "../../utils/language";

export const routeWithParameters = (url: string): string => {
  const searchParams = window.location.search;
  if (searchParams !== "") {
    return `${url}${searchParams}`;
  } else {
    return url;
  }
};

export function Header() {
  const [activeMenu, setActiveMenu] = React.useState(2);
  const [activeLink, setActiveLink] = React.useState(0);
  const { t, i18n } = useTranslation();

  const linkImages = [
    "5.jpg",
    "1.jpg",
    "2.jpg",
    "8.jpg",
    "12.jpg",
    "7.png",
    "3.jpg",
    "6.jpg",
    "11.jpg",
    "10.jpg",
    "9.jpg",
  ];

  const headerLink = {
    color: "white",
    cursor: "pointer",
    marginTop: "12px",
    marginBottom: "12px",
    _hover: {
      color: "red.500",
    },
  };

  const mobileHeaderLink = {
    color: "white",
    cursor: "pointer",
    _hover: {
      color: "red.500",
    },
  };

  return (
      <>
        {/* Desktop Menu */}
        <Desktop>
          <Box bg="black" pos="fixed" top="0" w="100%" zIndex={10}>
            <Flex
                d="grid"
                gridTemplateColumns="5fr 2fr 2fr 1fr 2fr 3fr 1fr"
                alignItems="center"
                maxW="1200px"
                p="4"
                m="0 auto"
            >
              <Link to={routeWithParameters(urls.Homepage)}>
                <Box>
                  <Image src="/assets/images/logo.png" height="28px" alt="" />
                </Box>
              </Link>
              <motion.div {...headerAnimate}>
                <Text
                    color={activeMenu === 0 ? "red.500" : "white"}
                    fontWeight="600"
                    onMouseEnter={() => setActiveMenu(0)}
                    cursor="pointer"
                    d="flex"
                    justifyContent="center"
                >
                  {t("Menu.Services")}
                  <Icon
                      ml="2"
                      mt="1"
                      name="fold"
                      transform={activeMenu === 0 ? "rotate(180deg)" : ""}
                      color={activeMenu === 0 ? "red.500" : "white"}
                  />
                </Text>
              </motion.div>
              <motion.div {...headerAnimate}>
                <Text
                    d="flex"
                    color={activeMenu === 1 ? "red.500" : "white"}
                    fontWeight="600"
                    textDecoration="none"
                    onMouseEnter={() => setActiveMenu(1)}
                    cursor="pointer"
                    justifyContent="center"
                >
                  {t("Menu.About")}
                  <Icon
                      ml="2"
                      mt="1"
                      name="fold"
                      transform={activeMenu === 1 ? "rotate(180deg)" : ""}
                      color={activeMenu === 1 ? "red.500" : "white"}
                  />
                </Text>
              </motion.div>
              <motion.div {...headerAnimate}>
                <PseudoBox
                    cursor="pointer"
                    color="white"
                    _hover={{
                      color: "red.500",
                    }}
                    d="flex"
                    justifyContent="center"
                    fontWeight="600"
                >
                  <a
                      href={routeWithParameters(urls.Blog)}
                      target="_blank"
                      rel="noopener"
                  >
                    Blog
                  </a>
                </PseudoBox>
              </motion.div>
              <motion.div {...headerAnimate}>
                <PseudoBox
                    cursor="pointer"
                    color="white"
                    _hover={{
                      color: "red.500",
                    }}
                    fontWeight="600"
                    onMouseEnter={() => setActiveMenu(2)}
                    d="flex"
                    justifyContent="center"
                >
                  <a
                      href={`${routeWithParameters(urls.Login)}${
                          window.location.search.length > 0
                              ? `&lang=${i18n.language}&signup=true`
                              : `?lang=${i18n.language}&signup=true`
                      }`}
                      target="_blank"
                      rel="noopener"
                  >
                    {t("Menu.Panel")}
                  </a>
                </PseudoBox>
              </motion.div>

              <CLink
                  bg="#5558ff"
                  color="white"
                  cursor="pointer"
                  p={3}
                  px={5}
                  borderRadius={"20px"}
                  textAlign="center"
                  mr={5}
                  lineHeight="normal"
                  fontWeight="bold"
                  _hover={{
                    textDecoration: "none",
                  }}
                  href={`${routeWithParameters(urls.Register)}${
                      window.location.search.length > 0
                          ? `&lang=${i18n.language}&signup=true`
                          : `?lang=${i18n.language}&signup=true`
                  }`}
                  target="_blank"
                  rel="noopener"
                  margin="0 30px"
              >
                {t("Menu.Register")}
              </CLink>

              <motion.div {...headerAnimate}>
                <Flex>
                  {
                    ['tr', 'en'].map((item, index) => {
                      return (
                          <Text
                              key={index.toString()}
                              fontWeight="600"
                              cursor="pointer"
                              marginRight="5px"
                              marginLeft="5px"
                              color="#fff"
                              onClick={() => {
                                Language.change(item);
                              }}
