import {
  Box,
  Flex,
  Link as CLink,
  Text,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Desktop } from "../../utils/hooks";
import { Language, urls } from "../../utils/language";

export const routeWithParameters = (url: string): string => {
  const searchParams = window.location.search;
  if (searchParams !== "") {
    return `${url}${searchParams}`;
  } else {
    return url;
  }
};

export function Header() {
  const [activeMenu, setActiveMenu] = React.useState(2);
  const [activeLink, setActiveLink] = React.useState(0);
  const { t, i18n } = useTranslation();

  const linkImages = [
    "5.jpg",
    "1.jpg",
    "2.jpg",
    "8.jpg",
    "12.jpg",
    "7.png",
    "3.jpg",
    "6.jpg",
    "11.jpg",
    "10.jpg",
    "9.jpg",
  ];

  const headerLink = {
    color: "white",
    cursor: "pointer",
    marginTop: "12px",
    marginBottom: "12px",
    _hover: {
      color: "red.500",
    },
  };

  const mobileHeaderLink = {
    color: "white",
    cursor: "pointer",
    _hover: {
      color: "red.500",
    },
  };

  return (
      <>
        {/* Desktop Menu */}
        <Desktop>
          <Box bg="black" pos="fixed" top="0" w="100%" zIndex={10}>
            <Flex
                d="grid"
                gridTemplateColumns="5fr 2fr 2fr 1fr 2fr 3fr 1fr"
                alignItems="center"
                maxW="1200px"
                p="4"
                m="0 auto"
            >
              <Link to={routeWithParameters(urls.Homepage)}> Fish to Whale</Link>

              <Box
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.Login)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.Login")}
                </a>
              </Box>

              <Box
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.HowToStart)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.HowToStart")}
                </a>
              </Box>


                <Box
                    cursor="pointer"
                    color="black"
                    _hover={{
                      color: "red.500",
                    }}
                    justifyContent="center"
                    fontWeight="600"
                >
                  <a
                      href={routeWithParameters(urls.Contact)}
                      target="_blank"
                      rel="noopener"
                  >
                    {t("Menu.Contact")}
                  </a>
                </Box>

              <motion.div {...headerAnimate}>
                <Flex>
                  {
                    ['tr', 'en'].map((item, index) => {
                      return (
                          <Text
                              key={index.toString()}
                              fontWeight="600"
                              cursor="pointer"
                              marginRight="5px"
                              marginLeft="5px"
                              color="#fff"
                              onClick={() => {
                                Language.change(item);
                              }}
                          >
                            {item.toUpperCase()}
                          </Text>
                      );
                    })}
                </Flex>
              </motion.div>
            </Flex>
          </Box>
        </Desktop>
      </>
  );
}
