import {
  Box,
  Flex,
  Link as CLink,
  Text,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Desktop } from "../../utils/hooks";
import { Language, urls } from "../../utils/language";

export const routeWithParameters = (url: string): string => {
  const searchParams = window.location.search;
  if (searchParams !== "") {
    return `${url}${searchParams}`;
  } else {
    return url;
  }
};

export function Header() {
  const { t, i18n } = useTranslation();

  // TODO: diğer başlıkları ekle
  return (
    <>
      {/* Desktop Menu */}
      <Desktop>
        <Box bg="white" pos="fixed" top="0" w="100%" zIndex={10}>
          <Flex
            display="grid"
            gridTemplateColumns="5fr 2fr 2fr 1fr 2fr 3fr 1fr"
            alignItems="center"
            maxW="1200px"
            p="4"
            m="0 auto"
          >
            <Link to={routeWithParameters(urls.Homepage)}>
            </Link>
            <CLink
              bg="#5558ff"
              color="white"
              cursor="pointer"
              p={3}
              px={5}
              borderRadius={"20px"}
              textAlign="center"
              mr={5}
              lineHeight="normal"
              fontWeight="bold"
              _hover={{
                textDecoration: "none",
              }}
              href={`${routeWithParameters(urls.Register)}${
                window.location.search.length > 0
                  ? `&lang=${i18n.language}&signup=true`
                  : `?lang=${i18n.language}&signup=true`
              }`}
              target="_blank"
              rel="noopener"
              margin="0 30px"
            >
              {t("Menu.Register")}
            </CLink>
          </Flex>
        </Box>
      </Desktop>

    </>
  );
}
