import {
  Box,
  Flex, Spacer,
  Text,
} from "@chakra-ui/react";
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { Desktop } from "../../utils/hooks";
import { Language, urls } from "../../utils/language";

export const routeWithParameters = (url: string): string => {
  const searchParams = window.location.search;
  if (searchParams !== "") {
    return `${url}${searchParams}`;
  } else {
    return url;
  }
};

export function Header() {
  const { t } = useTranslation();

  const headerLink = {
    color: "white",
    cursor: "pointer",
    marginTop: "12px",
    marginBottom: "12px",
    _hover: {
      color: "red.500",
    },
  };

  return (
      <>
        {/* Desktop Menu */}
        <Desktop>
          <Box bg="white" pos="fixed" top="0" w="100%" zIndex={10}>
            <Flex
            >
              <Box p='4' bg='red.400'>
                  <Link to={routeWithParameters(urls.Homepage)}> Fish to Whale</Link>
              </Box>
              <Spacer />
              <Box
                  p='4'
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.Login)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.Login")}
                </a>
              </Box>

              <Box
                  p='4'
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.HowToStart)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.HowToStart")}
                </a>
              </Box>

              <Box
                  p='4'
                  cursor="pointer"
                  color="black"
                  _hover={{
                    color: "red.500",
                  }}
                  justifyContent="center"
                  fontWeight="600"
              >
                <a
                    href={routeWithParameters(urls.Contact)}
                    target="_blank"
                    rel="noopener"
                >
                  {t("Menu.Contact")}
                </a>
              </Box>

              <Flex>
                {
                  ['tr', 'en'].map((item, index) => {
                    return (
                        <Text
                            key={index.toString()}
                            fontWeight="600"
                            cursor="pointer"
                            marginRight="5px"
                            marginLeft="5px"
                            color="#fff"
                            onClick={() => {
                              Language.change(item);
                            }}
                        >
                          {item.toUpperCase()}
                        </Text>
                    );
                  })}
              </Flex>
            </Flex>
          </Box>
        </Desktop>
      </>
  );
}
